import React from 'react';
import { ListGroupItem } from 'reactstrap';
import {Link} from 'react-router-dom'
import './CityBox.css'

class CityBox extends React.Component {
    constructor(props) { 
        super(props);
        this.state = {
            city: []
        };
    }

    render() {  
        return(
            <div className="CityBox">
                <Link to = {{ pathname: "/itineraries/" + this.props.city._id }}>
                    <ListGroupItem >
                        <h5></h5>{this.props.city.name}
                    </ListGroupItem> 
                </Link>
            </div>
        )
    }
}

export default CityBox;
