import { GET_CITIES, DATA_LOADING, FILTER_CITIES } from './types';
import axios from 'axios';

export const getCities = () => dispatch => {
    dispatch(citiesLoading());
    axios
        .get('http://localhost:5000/cities/all')
        .then(res => dispatch({
            type: GET_CITIES,
            payload: res.data
        }))
}

export const citiesLoading = () => {
    return {
        type: DATA_LOADING 
    }
}

export const filterCities = (e) => { console.log
    return {
        type: FILTER_CITIES,
        payload: e
    }
}
