import React, { useState } from 'react';
import { connect } from 'react-redux';
import './AppNavBar.css'
import { Collapse, Navbar, NavbarToggler, Nav, NavItem, NavLink } from 'reactstrap';
import { UncontrolledDropdown,DropdownToggle,DropdownMenu,DropdownItem } from 'reactstrap';
import { Link } from  'react-router-dom';
import userIcon from '../../assets/images/userIcon.png';

import { logOut } from '../'

const AppNavBar = (props) => {
  const [collapsed, setCollapsed] = useState(true);
  const toggleNavbar = () => setCollapsed(!collapsed);
  const isLogged = props.isLogged;

  return (
    <div className = "AppNavBar">
      {console.log(isLogged)}
        <Navbar className="Navbar" color="faded" light>
          <UncontrolledDropdown nav inNavbar  className="userIcon" >
            <DropdownToggle nav>
              <img className="UserIconNavBar" src={userIcon} alt="User" />
            </DropdownToggle>
            <DropdownMenu left="true">
              <DropdownItem>
                <Link to="/createAccount">
                        Create Account
                </Link>
              </DropdownItem>
              <DropdownItem> 
                { isLogged ? (
                  <Link to="/">
                          Log Out
                  </Link> 
                ) : (
                  <Link to="/LogIn">
                          Log In
                  </Link>
                )
                }
              </DropdownItem>
                    {/* <DropdownItem divider />
                    <DropdownItem>
                    Reset
                    </DropdownItem> */}
            </DropdownMenu>
          </UncontrolledDropdown>
        {/* Boton perfil */}
        {/* <NavbarBrand href="/">MYtinerary</NavbarBrand> */}
        {/* Boton Menu */}
        <NavbarToggler onClick={toggleNavbar} className="mr-2" />
        <Collapse isOpen={!collapsed} navbar>
          <Nav navbar>
            <NavItem>
              <NavLink href="/components/">Components</NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="https://github.com/reactstrap/reactstrap">GitHub</NavLink>
            </NavItem>
          </Nav>
        </Collapse>
        {/* Boton menu */}
      </Navbar>
    </div>
  );
}

const mapStateToProps = (state) => {
  //Voy a tomar el estado y lo voy a generar como propiedad para el componente\
  return {
      isLogged: state.userReducer.isLogged,
  };
};

//regresa creadores de acciones (action creators)
const mapDispatchToProps = (dispatch) => {
  return {
      logOut: () => dispatch(logOut()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(AppNavBar);
