import { GET_CITIES, DATA_LOADING, GET_ITINERARIES } from '../actions/types';

const initialState = {
    cities: [],
    loading: false,
}


export default function(state = initialState, action) {
    // {
    //     type: GET_CITIES,
    //     payload: res.data
    // }
    switch (action.type) {
        case GET_CITIES:
            return {
                ...state,
                cities: action.payload,
                loading: false
            };
        case GET_ITINERARIES:
            return {
                ...state,
                itineraries: action.payload,
                loading: false
            }
        case DATA_LOADING:
            return {
                ...state,
                loading: true
            }
        default:
            return state; 
    }
}
