import React from 'react';
import { Col, Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import { connect } from 'react-redux';
import AppNavBar from '../AppNavbar/AppNavBar';
import Footer from '../Footer/Footer';
import CreateAccountForm from '../CreateAccountForm/CreateAccountForm';

import './CreateAccountPage.css'

import { createAccount } from '../../actions/userActions';

class CreateAccountPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
                firstName: "",
                lastName: "",
                username: "",
                password: "",
                email: "",
                profilePic: null
        }
    }
    onChangeData= (e) => {
        var state = this.state;
        state[e.target.name] = e.target.value;
        this.setState(state)
    }

    onChangeImage = (e) => { 
        this.setState({
            profilePic: e.target.files[0]
        })
    }

    create = () => {
        const fd = new FormData();
        fd.append('firstName', this.state.firstName)
        fd.append('lastName', this.state.lastName)
        fd.append('password', this.state.password)
        fd.append('username', this.state.username)
        fd.append('email', this.state.email)
        fd.append('profilePic', this.state.profilePic);
        console.log(this.state.profilePic);
        this.props.createAccount(fd);
        this.setState( {
            firstName: "",
                lastName: "",
                username: "",
                password: "",
                email: "",
                profilePic: null
        })

    }
    //Tip: your component state should store all the input field values and dispatch an action only when the user clicks on submit.
    //username, password, email, firstname, lastname, profilePic, country voy a recolectar
    render() { 
        return (
            <div>
                <AppNavBar/>
                <CreateAccountForm/>
                <Footer/>
            </div>
        )
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(CreateAccountPage);
