import React from 'react';
import { connect } from 'react-redux';
import { ListGroup, ListGroupItem, Label, Input } from 'reactstrap';
import AppNavBar from '../AppNavbar/AppNavBar';
import CityBox from '../CityBox/CityBox';
import './Cities.css';

// actions importadas
import { getCities } from '../../actions/cityActions';

class CitiesPage extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            cities: [],
            animales: []
        };
    }

    async componentDidMount() {
        await this.props.getCities(); // llamo a la funcion que genera como prop en el dispatch
        this.setState({ cities: this.props.cities})
        console.log('list')
    }
    // filterCities = () => {

    // }

    render() {   
        return(
            <div>  
                <AppNavBar/>
                {/* <Input type="text" name="cityFilter" id="cityFilter" 
                        placeholder="Search your city..." onChange={this.filterCities}/> */}
                {this.state.cities.map(pepe => <cityBox itinerary={pepe}/>)}
               {/* {this.props.cities.map(city => 
                    <CityBox city={city}></CityBox>)} */}
               {/* <QueryCitiesList cities={this.state.filteredCities} name={"asd"} /> */}
              
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    //store.getState();
    //Voy a tomar el estado y lo voy a generar como propiedad para el componente
    return {
        cities: state.cities,
    };
};

//regresa creadores de acciones (action creators)
const mapDispatchToProps = (dispatch) => {
    return {
        getCities: () => dispatch(getCities())
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(CitiesPage);
