import React from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css'
import './App.css';
import { connect } from 'react-redux';

//Paths
import Home from './components/HomePage/Home';
import Cities from './components/CitiesPage/Cities';
import Itineraries from './components/ItinerariesPage/ItinerariesPage';
import CreateAccountPage from './components/CreateAccountPage/CreateAccountPage';
import LogInPage from './components/LogInPage/LogInPage';

class App extends React.Component {
  constructor(props) {
    super(props); 
    this.state = {
      isLogged : false,
      user: {
        username : ""
      }
    }
  }


  render() {
    return (
      <Router>
        <div className="App">
          <Switch>
            <Route exact path="/" component={Home}/>
            <Route path="/cities/all" component={Cities}/>
            <Route path="/cities/:cityId" component={Itineraries} />
            <Route path="/createAccount" component={CreateAccountPage}/>
            <Route path='/LogIn' component={LogInPage}/>
          </Switch>
        </div>
      </Router>
    );
  }
}

const mapStateToProps = state => {
  //Voy a tomar el estado y lo voy a generar como propiedad para el componente\
  return {
      user: state.userReducer.user,
  };
};

const mapDispatchToProps = reducer => {
  return { user: reducer.user };
};

export default connect(mapDispatchToProps, mapStateToProps)(App);
