import { GET_CITIES, DATA_LOADING } from './types';
import axios from 'axios';

export const getCities = () => awaitdispatch => {
    dispatch(citiesLoading());
    axios
        .get('http://localhost:5000/cities/all')
        .then(res => dispatch({
            type: GET_CITIES,
            payload: res.data
        }))
}

export const citiesLoading = () => {
    return {
        type: DATA_LOADING 
    }
}
