import React from 'react';
import { connect } from 'react-redux';
import { ListGroup, Input } from 'reactstrap';
import AppNavBar from '../AppNavbar/AppNavBar';
import CityBox from '../CityBox/CityBox';
import './Cities.css';
// actions importadas
import { getCities, filterCities } from '../../actions/cityActions';

class CitiesPage extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            cities1: [],
            loading: false
        };
    }

    //this.setState({animal : 'qwe' })

    // filterCities = (e) => {
    //     let filteredCities = this.props.cities; //recibo las
    //     filteredCities = filteredCities.filter((city) => {
    //         let cityName = city.name.toLowerCase()
    //         return cityName.indexOf(
    //             e.target.value.toLowerCase()) === 0
    //     })
    //     this.setState({cities: filteredCities })
    // }

     async componentDidMount() {
        await this.props.getCities() // llamo a la funcion que genera como prop en el dispatch
        this.setState({
            cities1: this.props.cities,
            loading: this.props.loading
        })
        console.log('asd')
        console.log(this.props)
        console.log()
        this.filterCities()
    }

     filterCities = (e) => { 
        let filteredCities = this.props.cities; //recibo las
        filteredCities = filteredCities.filter((city) => {
          let cityName = city.name.toLowerCase() + city.country.toLowerCase()
          return cityName.indexOf(
            e.target.value.toLowerCase()) === 0
        })
        this.setState({
          cities1: filteredCities
        })
       }

    render() {  
        // console.log('props');
        const asd = this.props.cities
        console.log(asd);
        console.log("render");

        console.log(this.props); //contiene el metodo getCities y el array cities
        //props se mantienen estable a partir de que se crea el proyecto
        console.log(this.state); //Hace referencia al estado actual, no tiene 
        // console.log('estado ');
        // // const  asd  = this.props.cities;
        // // console.log({asd});
        // // this.setState({ cities: this.props.cities })
        // console.log( this.state.filteredCities ) 
        // this.setState({cities:this.props.cities})
        // this.setState({ filterCities: this.props.filterCities})
           
        return(
            <div>  
                <AppNavBar/>
                <Input type="filter" name="cityFilter" id="cityFilter" 
                        placeholder="Search your city..." onChange={this.filterCities}/>
                { this.props.loading === true && <h1>ESPERA</h1> }
                <ListGroup>
                    {/* {cities.map( (city) => <CityBox key={city._id} city={city}/> )}               */}
                </ListGroup>
            </div>
        )
    }
}

//ACA ARRANCA EL PROGRAMA
const mapStateToProps = (state) => {
    //Voy a tomar el estado y lo voy a generar como propiedad para el componente\
    console.log(state)
    return {
        cities1: state.cities.cities,
        loading: state.cities.loading,
    };
};

//regresa creadores de acciones (action creators)
const mapDispatchToProps = (dispatch) => {
    return {
        getCities: () => dispatch(getCities()),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(CitiesPage);
