import React from 'react';
import { Col, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import axios from 'axios';
import { connect } from 'react-redux';
import { logIn } from '../../actions/userActions'
import { logInGoogle } from '../../actions/userActions'
import { FaGoogle } from 'react-icons/fa';
import './MainLoginForm.css'

class MainLoginForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email : "",
            password: "",
            isLogged: false
        }
    }

    logIn = async() => {
        await this.props.logIn(this.state); 
    }

    logInGoogle = async() => {
        window.location.href = "http://localhost:5000/users/loginGoogle"
    }

    onChangeData= (e) => {
        var state = this.state;
        state[e.target.name] = e.target.value;
        this.setState(state)
    }

    render() {
        return (
            <div className="formLogin">
                <Row>
                    <Col><h5>Login</h5></Col>
                </Row>
                <Form>
                    <FormGroup row className="formGroup">
                        <Row>
                            <Col>
                                <Label for="email" sm={2}>Email</Label>
                            </Col>
                            <Col>
                            </Col>
                        </Row>
                        
                        <Col sm={10}>
                        <Input onChange={this.onChangeData} value={this.state.email} type="email" name="email" id="email" placeholder="" />
                        </Col>
                    </FormGroup>
                    <FormGroup row className="formGroup">
                        <Label for="password" sm={2}>Password</Label>
                        <Col sm={10}>
                        <Input  onChange={this.onChangeData} value={this.state.password} type="password" name="password" id="password" placeholder="" />
                        </Col>
                    </FormGroup> 

                    <FormGroup check row className="bottonLogin">
                        <Col sm={{ size: 5 }}>
                        <Button onClick={this.logIn}>Log In</Button>
                        </Col>
                    </FormGroup>

                    <FormGroup check row className="bottonLogin">
                        <Col sm={{ size: 5 }}>
                        <Button onClick={this.logInGoogle}> <FaGoogle/>oogle  </Button>
                        </Col>
                    </FormGroup>
                </Form>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        isLogged: state.userReducer.isLogged
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        logIn: (userData) => dispatch(logIn(userData)),
        logInGoogle: (userData) => dispatch(logInGoogle(userData))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(MainLoginForm);
