import React from 'react';
import { connect } from 'react-redux';
import { ListGroup, Label, Input } from 'reactstrap';
import propTypes from 'prop-types';
import AppNavBar from '../AppNavbar/AppNavBar';
import CityBox from '../CityBox/CityBox';
import './Cities.css';
// actions importadas
import { getCities } from '../../actions/cityActions';

class CitiesPage extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            filteredCities: [],
            animal: 'asd'
        };
    }

    //this.setState({animal : 'qwe' })

    filterCities = (e) => {
        let filteredCities = this.props.cities; //recibo las
        filteredCities = filteredCities.filter((city) => {
            let cityName = city.name.toLowerCase()
            return cityName.indexOf(
                e.target.value.toLowerCase()) === 0
        })
        this.setState({filteredCities: filteredCities })
    }

    async componentDidMount() {
        await this.props.getCities().then() // llamo a la funcion que genera como prop en el dispatch
        console.log(this.props.cities);
        await this.setState({filteredCities: this.props.cities});
        this.setState({animal : 'qwe' })
        // console.log(this.props.cities);
        // console.log(this.state.filteredCities);
        // this.setState({...this.state})
    }

    // filterCities = (e) => {
    //     let filteredCities = this.props.cities; //recibo las
    //     filteredCities = filteredCities.filter((city) => {
    //       let cityName = city.name.toLowerCase() + city.country.toLowerCase()
    //       return cityName.indexOf(
    //         e.target.value.toLowerCase()) === 0
    //     })
    //     this.setState({
    //       filteredCities: filteredCities
    //     })
    //   }

    render() {  
        // console.log('props');
        console.log(this.props); //contiene el metodo getCities y el array cities
        console.log(this.state); //Hace referencia al estado actual, no tiene 
        // console.log('estado ');
        // // const  asd  = this.props.cities;
        // // console.log({asd});
        // // this.setState({ cities: this.props.cities })
        // console.log( this.state.filteredCities )    
        return(
            <div>  
                <AppNavBar/>
                <Input type="filter" name="cityFilter" id="cityFilter" 
                        placeholder="Search your city..." onChange={this.filterCities}/>
                <ListGroup>
                    {this.state.filteredCities.map( (city) => <CityBox city={city}/> )}              
                </ListGroup>
            </div>
        )
    }
}

// CitiesPage.propTypes = {  
//     getCities: propTypes.func.isRequired,
//     cities: propTypes.object.isRequired  //representa el estado
//   }

const mapStateToProps = (state) => {
    //Voy a tomar el estado y lo voy a generar como propiedad para el componente\
    return {
        cities: state.cities.cities,
    };
};

//regresa creadores de acciones (action creators)
const mapDispatchToProps = (dispatch) => {
    return {
        getCities: () => dispatch(getCities())
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(CitiesPage);
