import { GET_CITIES, DATA_LOADING, GET_ITINERARIES } from './types';
import axios from 'axios';
axios.defaults.headers.common['Authorization'] = `Bearer localStorage.getItem("token1")`;

// axios.interceptors.request.use(function (config) {
//     const token = localStorage.getItem("token");
//     config.headers.Authorization =  token;

//     return config;
// });


export const getCities = () => async dispatch => {
    dispatch(citiesLoading()); console.log(axios.defaults.headers.common['Authorization'])
    await axios
        .get('http://localhost:5000/cities/all')
        .then(res => dispatch({
            type: GET_CITIES,
            payload: res.data
        }))
}

export const citiesLoading = () => {
    return {
        type: DATA_LOADING 
    }
}

export const getItineraries = (cityId) => async dispatch => {
    dispatch(itinerariesLoading(cityId));
    await axios
        .get('http://localhost:5000/itineraries/' + cityId)
        .then(res => dispatch({
            type: GET_ITINERARIES,
            payload: res.data
        }))
}

export const itinerariesLoading = () => {
    return {
        type: DATA_LOADING 
    }
}
