import React from 'react';
import { connect } from 'react-redux';
import { Spinner, ListGroup } from 'reactstrap';

// actions importadas
import { getItineraries } from '../../actions/cityActions';

import AppNavBar from '../AppNavbar/AppNavBar';
import ItineraryBox from '../ItineraryBox/ItineraryBox';

class ItinerariesPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            itineraries: [],
            loading: false
        }
    }

    async componentDidMount() {
        console.log(this.props.match.params.cityId);
        await this.props.getItineraries(this.props.match.params.cityId);
        this.setState({ 
            itineraries: this.props.itineraries,
            loading: this.props.loading
        });
        console.log(this.state)
    }

    render() {
        return( 
            <div className="itinerariesPage">
            <AppNavBar/>
                {/* { this.props.loading === true && 
                    <div>
                        <Spinner style={{ width: '3rem', height: '3rem' }} />{' '}
                        <Spinner style={{ width: '3rem', height: '3rem' }} />{' '}
                        <Spinner style={{ width: '3rem', height: '3rem' }} />{' '}
                    </div>
                }
                { this.props.loading === false &&  
                    <ListGroup> 
                        {this.state.itineraries.map( (itinerary) => <ItineraryBox key={itinerary._id} itinerary={itinerary}/> )}              
                    </ListGroup>
                } */}
            </div>
        )
    }
}

const mapStateToProps = state => {
    //Voy a tomar el estado y lo voy a generar como propiedad para el componente\
    return {
        itineraries: state.cities.itineraries,
        loading: state.cities.loading,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        getItineraries: (cityId) => dispatch(getItineraries(cityId)),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(ItinerariesPage);
