import { GET_CITIES, DATA_LOADING, GET_ITINERARIES } from './types';
import axios from 'axios';

export const getCities = () => async dispatch => {
    dispatch(citiesLoading());
    await axios
        .get('http://localhost:5000/cities/all')
        .then(res => dispatch({
            type: GET_CITIES,
            payload: res.data
        }))
}

export const citiesLoading = () => {
    return {
        type: DATA_LOADING 
    }
}

export const getItineraries = () => async dispatch => {
    dispatch(itinerariesLoading());
    await axios
        .get('http://localhost:5000/cities/:')
        .then(res => dispatch({
            type: GET_ITINERARIES,
            payload: res.data
        }))
}
