import { CREATE_ACCOUNT, LOG_OUT, LOG_IN, LOGIN_SUCCESS } from '../actions/types';

const initialState = {
    isLogged : false,
    user: [],
    token : ""
}

export default function(state = initialState, action) {
    switch (action.type) {
        case CREATE_ACCOUNT:
            console.log('reducer')
            return {
                ...state
            };
            case LOG_OUT:
                localStorage.removeItem('token')
                return {
                    ...state,
                    isLogged: false,
                    token: ""
                }
            case LOG_IN: console.log('REDUCER'); 
            window.location.href = `http://localhost:3000/?${this.props.token}`;    
                localStorage.setItem('token', action.payload.token)
                return {
                    ...state,
                    isLogged: (action.payload.status == 200),
                    // token: action.payload.data.token
                }
            case LOGIN_SUCCESS:
                localStorage.setItem('token', action.payload.token)
                return {
                    ...state,
                    user: action.payload.data,
                    isLogged: true
                }
        default:
            return state;
    }
}
