import { GET_CITIES, DATA_LOADING, GET_ITINERARIES } from './types';
import axios from 'axios';
axios.defaults.headers.common['Authorization'] = localStorage.getItem("token");

export const getCities = () => async dispatch => {
    dispatch(citiesLoading());
    await axios
        .get('http://localhost:5000/cities/all',
        )
        .then(res => dispatch({
            type: GET_CITIES,
            payload: res.data
        }))
}

export const citiesLoading = () => {
    return {
        type: DATA_LOADING 
    }
}

export const getItineraries = (cityId) => async dispatch => {
    dispatch(itinerariesLoading(cityId));
    await axios
        .get('http://localhost:5000/itineraries/' + cityId)
        .then(res => dispatch({
            type: GET_ITINERARIES,
            payload: res.data
        }))
}

export const itinerariesLoading = () => {
    return {
        type: DATA_LOADING 
    }
}
