import { GET_ACTIVITIES } from './types';
import axios from 'axios';

export const getActivities = () => async dispatch => {
    console.log('activities Action');
    await axios
        .get('http://localhost:5000/activities/:itineraryId')
        .then(res => dispatch({
            type: GET_ACTIVITIES,
            payload: res.data
        }))
}
