import { GET_COMMENT } from './types';
import axios from 'axios';


export const getComments = (itineraryId) => async dispatch => {

    console.log(localStorage.getItem("token"));
 
    await axios
        .get( `http://localhost:5000/comments/${itineraryId}` )
        .then(res => dispatch({
            type: GET_CITIES,
            payload: res.data
        }))
}
