import { CREATE_ACCOUNT, LOG_IN, LOG_IN_GOOGLE, LOGIN_SUCCESS } from './types';
import axios from 'axios';

export const createAccount = (userData) => async dispatch => {
    console.log(userData);
    await axios
        .post('http://localhost:5000/users/signup', userData)
        .then( res => dispatch({
            type: CREATE_ACCOUNT,
            payload: res.data
        }))
        .catch( (err) => console.log(err))
}

export const logIn = (userData) => async dispatch => {
    await axios
        .post('http://localhost:5000/users/login', userData)
        .then( res => dispatch({
            type: LOG_IN,
            payload: res.data
        }))
}

export const logInGoogle = (userData) => async dispatch => {
        console.log('USER ACTION');
        console.log(userData);
        dispatch({
            type: LOG_IN_GOOGLE,
            payload: userData
        })
    
}

export const logOut = () => async dispatch => {
    dispatch({
        type: LOG_IN_GOOGLE,
        payload: userData
    })
}

