import React from 'react';
import { Col, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import axios from 'axios';
import { connect } from 'react-redux';
import { logIn } from '../../actions/userActions'
import './LogInPage.css'
import AppNavBar from '../AppNavbar/AppNavBar'
import Footer from '../Footer/Footer'

class LogInPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email : "",
            password: "",
            user: []
        }
    }

    // async componentDidMount() {
    //     await axios
    //         .get('http://localhost:5000/users/login')
    //         .then( res => { ad = res.data[0]})
    //         .then( ad => console.log(ad));
    //     await this.setState({asd: ad.profilePic})
    //     await    console.log(this.state.asd)

    // }

    aslogIn = () => {
        this.props.logIn(this.state);
        this.setState( {
            password: "",
            email: "",
        })
        console.log(this.props.user.token);
        
        localStorage.setItem("token",this.props.user.token);
    }

    onChangeData= (e) => {
        var state = this.state;
        state[e.target.name] = e.target.value;
        this.setState(state)
    }

    render() {

        return (
            <div>
                <AppNavBar/>
                <Form>
                    {/* <FormGroup row className="formGroup">
                        <Label for="username" sm={2}>Username</Label>
                        <Col sm={10}>
                        <Input type="text" name="username" id="username" placeholder="" />
                        </Col>
                    </FormGroup> */}
                     <FormGroup row className="formGroup">
                        <Label for="email" sm={2}>Email</Label>
                        <Col sm={10}>
                        <Input onChange={this.onChangeData} value={this.state.email} type="email" name="email" id="email" placeholder="" />
                        </Col>
                    </FormGroup>
                    <FormGroup row className="formGroup">
                        <Label for="password" sm={2}>Password</Label>
                        <Col sm={10}>
                        <Input  onChange={this.onChangeData} value={this.state.password} type="password" name="password" id="password" placeholder="" />
                        </Col>
                    </FormGroup> 

                    <FormGroup check row >
                        <Col sm={{ size: 10 }}>
                        <Button onClick={this.logIn}>Log In</Button>
                        </Col>
                    </FormGroup>
                </Form>
                <Footer/>
            </div>
        )
    }
}

const mapStateToProps = state => {
    //Voy a tomar el estado y lo voy a generar como propiedad para el componente\
    return {
        user: state.userReducer.user
    };
};

//regresa creadores de acciones (action creators)
const mapDispatchToProps = (dispatch) => {
    return {
        logIn: (userData) => dispatch(logIn(userData)),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(LogInPage);
