import React from 'react';
import { connect } from 'react-redux';
import { ListGroup, ListGroupItem, Label, Input } from 'reactstrap';
import AppNavBar from '../AppNavbar/AppNavBar';
import './Cities.css';

// actions importadas
import getCities from '../../actions/cityActions';

class CitiesPage extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            cities: []
        };
    }

    // filterCities = () => {

    // }

    render() {   console.log(this.props);
        return(
            <div>
                <AppNavBar/>
                <Input type="text" name="cityFilter" id="cityFilter" 
                        placeholder="Search your city..." onChange={this.filterCities}/>

                <ListGroup>
                    <ListGroupItem>Cras justo odio</ListGroupItem>
                    <ListGroupItem>Dapibus ac facilisis in</ListGroupItem>
                    <ListGroupItem>Morbi leo risus</ListGroupItem>
                    <ListGroupItem>Porta ac consectetur ac</ListGroupItem>
                    <ListGroupItem>Vestibulum at eros</ListGroupItem>
                </ListGroup>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    //store.getState();
    //Voy a tomar el estado y lo voy a generar como propiedad para el componente
    return {
        cities: state.cities,
        hola: 123
    };
};

//regresa creadores de acciones (action creators)
const mapDispatchToProps = (dispatch) => {
    return {
        getCities: () => dispatch(getCities())
    }
}

export default connect(mapStateToProps)(CitiesPage);
