import React from 'react';
import { Col, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import axios from 'axios';
import './LogInPage.css'
var ad = "";

class LogInPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email : "",
            password: ""
        }
    }

    async componentDidMount() {
        await axios
            .get('http://localhost:5000/users/login')
            .then( res => { ad = res.data[0]})
            .then( ad => console.log(ad));
        await this.setState({asd: ad.profilePic})
        await    console.log(this.state.asd)

    }

    create = () => {
        const fd = new FormData();
        fd.append('firstName', this.state.firstName)
        fd.append('lastName', this.state.lastName)
        fd.append('password', this.state.password)
        fd.append('username', this.state.username)
        fd.append('email', this.state.email)
        fd.append('profilePic', this.state.profilePic);
        console.log(this.state.profilePic);
        this.props.createAccount(fd);
        this.setState( {
            firstName: "",
                lastName: "",
                username: "",
                password: "",
                email: "",
                profilePic: null
        })

    }
    
    onChangeData= (e) => {
        var state = this.state;
        state[e.target.name] = e.target.value;
        this.setState(state)
    }

    render() {
        return (
            <div>
                <Form>
                    {/* <FormGroup row className="formGroup">
                        <Label for="username" sm={2}>Username</Label>
                        <Col sm={10}>
                        <Input type="text" name="username" id="username" placeholder="" />
                        </Col>
                    </FormGroup> */}
                     <FormGroup row className="formGroup">
                        <Label for="email" sm={2}>Email</Label>
                        <Col sm={10}>
                        <Input onChange={this.onChangeData} value={this.state.email} type="email" name="email" id="email" placeholder="" />
                        </Col>
                    </FormGroup>
                    <FormGroup row className="formGroup">
                        <Label for="password" sm={2}>Password</Label>
                        <Col sm={10}>
                        <Input  onChange={this.onChangeData} value={this.state.password} type="password" name="password" id="password" placeholder="" />
                        </Col>
                    </FormGroup> 

                    <FormGroup check row >
                        <Col sm={{ size: 10 }}>
                        <Button onClick={this.create}>Log In</Button>
                        </Col>
                    </FormGroup>
                </Form>
            </div>
        )
    }
}

export default LogInPage;
